}

#registration .field,
#registration .form-control {
  position: relative;
  vertical-align: top;
  border: 1px solid #DDD;
  display: -moz-inline-stack;
  display: inline-block;
  color: #626262;
  outline: none;
  background: #fff;
  height: 36px;
  width: 100%;
  border-radius: 3px;
  font-size: 13px;
}

#registration .input-group-lg > .form-control {
