function countDown() {
    var countDay = $(".counter-days");
    var countHour = $(".counter-hours");
    var countMinutes = $(".counter-minutes");
    var countSeconds = $(".counter-seconds");
    var date = new Date("Jan 1, 2023 12:00:00");
    var interval = setInterval(function () {
        var today = new Date().getTime();
        var distance = date - today;
        countDay.html(Math.floor(distance / (1000 * 60 * 60 * 24)) + 'D');
        countHour.html(Math.floor(distance % (1000 * 60 * 60 * 24) / (1000 * 60 * 60)) + 'H');
        countMinutes.html(Math.floor(distance % (1000 * 60 * 60) / (1000 * 60)) + 'M');
        countSeconds.html(Math.floor(distance % (1000 * 60) / 1000) + 'S');
        if (distance < 0) {
            clearInterval(interval);
            $(".counter-days").html("00");
            $(".counter-hours").html("00");
            $(".counter-minutes").html("00");
            $(".counter-seconds").html("00");
        }
    }, 1000);
}
countDown();




//    dark light mood
var setDarkMode = (active = false) => {
    var wrapper = document.querySelector(":root");
    if (active) {
        wrapper.setAttribute("data-bs-theme", "dark");
        localStorage.setItem("theme", "dark");
    } else {
        wrapper.setAttribute("data-bs-theme", "light");
        localStorage.setItem("theme", "light");
    }
};

var toggleDarkMode = () => {
    var theme = document.querySelector(":root").getAttribute("data-bs-theme");
    // If the current theme is "light", we want to activate dark
    setDarkMode(theme === "light");
};

var initDarkMode = () => {
    var theme = localStorage.getItem("theme", "light");

    if(theme == "dark"){

        setDarkMode(true);
    }else{
        setDarkMode(false);
    }


    var toggleButton = document.querySelector(".tt-theme-toggle");
    toggleButton.addEventListener("click", toggleDarkMode);
};

initDarkMode();

// TT Kohost Custom Functions
jQuery(document).ready(function() {
    // Business type selection
    jQuery('.business-type-card input[type="radio"]').change(function() {
        jQuery('.business-type-card .card').removeClass('selected');
        jQuery(this).closest('.business-type-card').find('.card').addClass('selected');
    });

    // Service card interactions
    jQuery('.service-card.locked').click(function(e) {
        e.preventDefault();
        var serviceName = jQuery(this).find('.card-title').text();
        if (confirm(' ' + serviceName + '  .      ')) {
            // Redirect to purchase page
            window.location.href = 'cart.php';
        }
    });

    // Auto-hide notifications after 10 seconds
    setTimeout(function() {
        jQuery('.tt-notifications .alert').fadeOut();
    }, 10000);

    // Form validation for custom forms
    jQuery('.tt-custom-forms form').submit(function(e) {
        var isValid = true;
        jQuery(this).find('input[required], select[required], textarea[required]').each(function() {
            if (!jQuery(this).val()) {
                jQuery(this).addClass('is-invalid');
                isValid = false;
            } else {
                jQuery(this).removeClass('is-invalid');
            }
        });

        if (!isValid) {
            e.preventDefault();
            alert('      .');
        }
    });

    // Copy referral link functionality
    if (typeof copyReferralLink === 'undefined') {
        window.copyReferralLink = function() {
            var copyText = document.getElementById("referralLink");
            if (copyText) {
                copyText.select();
                copyText.setSelectionRange(0, 99999);
                document.execCommand("copy");

                // Show success message
                var btn = jQuery('.btn:contains("")');
                var originalText = btn.html();
                btn.html('<i class="fas fa-check"></i>  ');
                btn.addClass('btn-success').removeClass('btn-primary');

                setTimeout(function() {
                    btn.html(originalText);
                    btn.removeClass('btn-success').addClass('btn-primary');
                }, 2000);
            }
        };
    }

    // Real-time form preview for website forms
    jQuery('input[name="website_name"]').on('input', function() {
        var name = jQuery(this).val();
        if (name) {
            jQuery('#website-preview-name').text(name);
        }
    });

    // Service unlock functionality
    if (typeof unlockService === 'undefined') {
        window.unlockService = function(serviceId) {
            if (confirm('      ')) {
                // You can customize this based on your WHMCS setup
                window.location.href = 'cart.php?a=add&pid=' + serviceId;
            }
        };
    }
});

