}
.dropdown-toggle::after{
  vertical-align: middle;
}
.affix{
  transition: all 0.3s ease-in-out;
  animation: 300ms ease-in-out fadeInDown;
}
.tt-header.affix{
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  z-index: 1030;
